<?phpinclude_once '../comun/session.php';require_once '../comun/class.admin.php';require_once '../comun/BD.php';if ($_SESSION['USUARIO_TIPO'] != 'vendedor') {    echo 'No autorizado';    exit;}$CAT    = new Catalogo();?><script>    var search_admin = new Search({        path             : '',        region           : $("#sucursal-region"),              ciudad           : $("#sucursal-ciudad"),          });</script><script>function googleMap(params) {    var param_container   = params.container;    var param_icon        = params.icon;    var param_lat         = params.lat;    var param_lng         = params.lng;    var mapProp = {        center    : new google.maps.LatLng(param_lat, param_lng),        zoom      : 4,        mapTypeId : google.maps.MapTypeId.ROADMAP    };    var map = new google.maps.Map(document.getElementById($(param_container).attr("id")), mapProp);    var marker = new google.maps.Marker({        map      : map,        icon     : param_icon    });    google.maps.event.addListener(map, 'tilesloaded', function() {        if (!tilesloaded) {            tilesloaded++;            google.maps.event.trigger(map, 'resize');            map.setCenter(mapProp.center);        }                });    google.maps.event.addListener(map, 'zoom_changed', function() {    });    google.maps.event.addListener(map, 'click', function(event) {        marker.setMap(null);            marker = new google.maps.Marker({            map      : map,            position : event.latLng,            icon     : param_icon        });                           google.maps.event.addListener(marker, 'click', function() {            marker.setMap(null);               $("#sucursal-map").val('');        });                         $("#sucursal-map").val(event.latLng.lat()+","+event.latLng.lng());    });          google.maps.event.addListener(marker, 'click', function() {        marker.setMap(null);           $("#sucursal-map").val('');    });     }var marker;var tilesloaded = 0;var googleMap = googleMap({    container : $("#map_canvas"),    lat       : -33.4370047647916,    lng       : -70.63440598547459,    icon      : 'include/img/map_icon.png'            });    google.maps.event.addDomListener(window, 'load', googleMap);</script>    <style>     #form-add-sucursal {        width: 500px;    }        .admin-add-sucursal{        font-family:Verdana;        font-size:13px;        width: 300px;    }</style><form id="form-add-sucursal" action="app/admin/admin-request.php" enctype="multipart/form-data" method="POST"><div class="admin-add-sucursal">    <p><b>Agregar Sucursal</b></p><br/>    <span>Rut</span>    <p><input type="text" id="sucursal-rut"><p/>    <span>Nombre</span>    <p><input type="text" id="sucursal-nombre"><p/>    <span>Teléfono</span>    <p><input type="text" id="sucursal-telefono"><p/>    <span>Email</span>    <p><input type="text" id="sucursal-email"><p/>    <span>Fax</span>    <p><input type="text" id="sucursal-fax"><p/>    <span>Razón social</span>    <p><input type="text" id="sucursal-razonsocial"><p/>    <span>Logo</span>    <p><input type="file" id="sucursal-logo" name="logo" onchange="        admin.createPreviewImage({             input       : this,             preview     : $('#sucursal-logo-preview'),             cancel      : $('#sucursal-logo-cancel'),             default_img : 'include/img/logo_150x150.png'         });    "><p/>    <img id="sucursal-logo-preview" src="include/img/logo_150x150.png">    <br/>    <span id="sucursal-logo-cancel" style="display: none"><input type="checkbox" id="sucursal-logo-cancel-chk" name="logo_chk" onchange="        admin.deletePreviewImage({            input       : $('#sucursal-logo'),             preview     : $('#sucursal-logo-preview'),             cancel      : $('#sucursal-logo-cancel'),             cancel_chk  : $('#sucursal-logo-cancel-chk'),             default_img : 'include/img/logo_150x150.png'         });    "> Quitar imagen</span>    <br/><br/>    <span>Sitio web</span>    <p><input type="text" id="sucursal-url"><p/>    <span>Mapa</span>    <div id="map">        <div id="map_canvas" style="height:230px;width:400px;"></div>    </div>        <p><input type="hidden" id="sucursal-map"><p/>        <span>Dirección</span>    <p><input type="text" id="sucursal-direccion"><p/>    <span>        <label>Región</label>        <p>        <select id="sucursal-region" onchange="search_admin.getCiudades()">            <option value="" selected="selected">.:: TODAS ::.</option>            <?            $getRegiones = $CAT->getRegiones(                                    array(                                        'REGION_ID_PAIS' => 'CL'                                                        )                                );            while($row = $getRegiones->fetch_assoc()) { ?>                <option value="<?=$row['REGION_ID_REGION']?>"><?=mb_strtoupper($row['REGION_NOMBRE'])?></option>            <? } ?>        </select>        </p>    </span>    <br/>    <span>        <label>Ciudad</label>        <p>        <select id="sucursal-ciudad">            <option value="" selected="selected">.:: TODAS ::.</option>            <?            $getCiudades = $CAT->getCiudades(                                    array(                                        'CIUDAD_ID_PAIS' => 'CL'                                                        )                                );            while($row = $getCiudades->fetch_assoc()) { ?>                <option class="search-dynamic" value="<?=$row['CIUDAD_ID_CIUDAD']?>"><?=mb_strtoupper($row['CIUDAD_NOMBRE'])?></option>            <? } ?>        </select>        </p>    </span>    <br/>    <span>Horario Lunes a Viernes</span>    <p><input type="text" id="sucursal-horario-lunvie"><p/>    <span>Horario Sábado</span>    <p><input type="text" id="sucursal-horario-sab"><p/>    <span>Horario Domingo</span>    <p><input type="text" id="sucursal-horario-dom"><p/></div>        <div id="sucursal-submit-loader"></div><span class="btn rgt" onclick="admin.addSucursal()">Agregar</span><span class="btn rgt" onclick="$.fancybox.close()">Cancelar</span></form>